import React, { useContext } from 'react';
import { Songs } from '../Context';

export default function DetailSong() {
	const { song } = useContext(Songs);
	return (
		<div className="col-span-1 p-3 relative">
			<div className="absolute w-[80%] inset-0 top-10 items-center">
				<h2 className="text-green-300 font-bold text-center uppercase">Now playing</h2>
				<h2 className="text-neutral-400 text-2xl text-center uppercase">{song.name}</h2>
				<div className="w-[240px] m-auto mt-10">
					<img className="w-full" src={song.links.images[0].url} alt="avatar" />
				</div>
				<div className="w-[70%]">
					<div className="justify-evenly items-center mt-10">
						<img className="w-[70px] rounded-full" src={song.links.images[1].url} alt="avatar" />
						<span className="text-xl text-white">{song.author}</span>
					</div>
				</div>
			</div>
		</div>
	);
}
